const crypto = require('crypto');

const secret = crypto.randomBytes(20);
console.log(secret.toString('hex')); // Store this securely

const timeStep = 30; // Time step in seconds
const T0 = 0; // Unix epoch
const currentTime = Math.floor(Date.now() / 1000); // Current Unix time
const T = Math.floor((currentTime - T0) / timeStep);

const timeBuffer = Buffer.alloc(8); // 8 bytes
for (let i = 0; i < 8; i++) {
  timeBuffer[7 - i] = T & 0xff;
  T >>>= 8;
}

const hmac = crypto.createHmac('sha1', secret);
hmac.update(timeBuffer);
const hmacResult = hmac.digest();

const offset = hmacResult[hmacResult.length - 1] & 0xf;
const binCode = (hmacResult[offset] & 0x7f) << 24
  | (hmacResult[offset + 1] & 0xff) << 16
  | (hmacResult[offset + 2] & 0xff) << 8
  | (hmacResult[offset + 3] & 0xff);

const token = binCode % (10 ** 6); // 6 digit code
console.log(token);

const notp = require('notp');
const crypto = require('crypto');

// For simplicity, we'll use the same key for all users
// In a real system, you'd want to generate a unique key for each user and store it securely
const secretKey = crypto.randomBytes(20);

function generateOTP() {
    // notp.totp.gen() generates a OTP that's valid for 30 seconds by default
    const otp = notp.totp.gen(secretKey);
    return otp;
}

function verifyOTP(otp) {
    // notp.totp.verify() checks the provided OTP
    // The window option allows codes from the past or future to be valid
    // Here, we allow codes from the last 30 seconds and the next 30 seconds to be valid
    const verification = notp.totp.verify(otp, secretKey, { window: 1 });
    return verification;
}

module.exports = {
    generateOTP,
    verifyOTP,
};

