export default async (req, res) => {
  console.log("getDeviceInfo API called");

  // Log PostgreSQL connection details (NOTE: Only for debugging! Remove sensitive data before deploying to production)
  console.log("PostgreSQL Connection Details:", {
    host: process.env.PGHOST_2,
    port: process.env.PGPORT_2,
    database: process.env.PGDATABASE_2,
    user: process.env.PGUSER_2,
  });

  const authHeader = req.headers.authorization;

  // Check if the authorization header exists
  if (!authHeader) {
    return res.status(401).json({ error: 'Unauthorized' });
  }

  const token = authHeader.split(' ')[1];

  try {
    console.log("Verifying token...");
    const payload = await verifier.verify(token);
    console.log('Token is valid. Payload:', payload);

    console.log("Fetching device info from database...");
    const { rows } = await pool.query(
      `SELECT 
        device_serial, 
        site.label, 
        site.tz, 
        STRING_AGG('dg:' || display_group_id || ' ' || grp.label, ', ') AS display_groups
      FROM 
        device 
      INNER JOIN 
        site USING(site_id) 
      INNER JOIN 
        device_display_group_map map USING(device_id) 
      INNER JOIN 
        display_group grp USING(display_group_id) 
      GROUP BY 
        device_serial, 
        site.label, 
        site.tz 
      ORDER BY 
        device_serial;`
    );
    console.log("Data fetched:", rows);
    res.status(200).json(rows);
  } catch (error) {
    console.error("Error occurred:", error);
    if (error.name === "TokenVerificationError") {
      res.status(401).json({ error: 'Unauthorized' });
    } else {
      res.status(500).json({ error: error.message });
    }
  }
};

