package org.example.rpsarena;

import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.TextView;

import androidx.appcompat.app.AppCompatActivity;

public class MainActivity extends AppCompatActivity {
    private TextView playerScoreTextView;
    private TextView opponentScoreTextView;
    private TextView movesTextView;

    private Game game;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        playerScoreTextView = findViewById(R.id.playerScoreTextView);
        opponentScoreTextView = findViewById(R.id.opponentScoreTextView);
        movesTextView = findViewById(R.id.movesTextView);

        game = new Game(this);
    }

    public void updateScores(int playerScore, int opponentScore) {
        playerScoreTextView.setText("Your Score: " + playerScore);
        opponentScoreTextView.setText("Opponent Score: " + opponentScore);
    }

    public void updateMovesText(String move) {
        movesTextView.setText("Your Move: " + move);
    }

    public void onMoveButtonClicked(View view) {
        Button button = (Button) view;
        String move = button.getText().toString();
        game.onMoveSelected(move);
    }

    public void onExitButtonClicked(View view) {
        game.exitGame();
        finish();
    }
}

package org.example.rpsarena;

import java.util.Random;

public class Game {
    private MainActivity activity;
    private Player player;
    private Player opponent;
    private GameLogic gameLogic;

    public Game(MainActivity activity) {
        this.activity = activity;
        this.gameLogic = new GameLogic();
        setupGame();
    }

    private void setupGame() {
        player = new Player("Player 1");
        opponent = new Player("Player 2");
        player.setOpponent(opponent);
        opponent.setOpponent(player);
        updateScores();
    }

    public void onMoveSelected(String move) {
        if (move.equalsIgnoreCase("exit")) {
            exitGame();
        } else {
            Moves playerMove = convertToMove(move);
            if (playerMove == null) {
                activity.updateMovesText("Invalid move. Please try again.");
            } else {
                Moves opponentMove = generateOpponentMove();
                String result = gameLogic.determineWinner(playerMove, opponentMove);
                updateScores(result);
                activity.updateMovesText("Your Move: " + move + "\nOpponent Move: " + opponentMove);
            }
        }
    }

    private Moves convertToMove(String input) {
        try {
            return Moves.valueOf(input.toUpperCase());
        } catch (IllegalArgumentException e) {
            return null;
        }
    }

    private Moves generateOpponentMove() {
        Moves[] moves = Moves.values();
        Random random = new Random();
        return moves[random.nextInt(moves.length)];
    }

    private void updateScores(String result) {
        if (result.equals("WIN")) {
            player.incrementPoints();
            activity.updateScores(player.getPlayerPoints(), opponent.getPlayerPoints());
        } else if (result.equals("LOSS")) {
            opponent.incrementPoints();
            activity.updateScores(player.getPlayerPoints(), opponent.getPlayerPoints());
        }
    }

    private void updateScores() {
        activity.updateScores(player.getPlayerPoints(), opponent.getPlayerPoints());
    }

    public void exitGame() {
        // Any cleanup or additional actions required on game exit
    }
}

#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <unistd.h>
#include <arpa/inet.h>

#define PORT 8888
#define BUFFER_SIZE 1024

int main() {
    int server_socket;
    struct sockaddr_in server_addr, client_addr;
    socklen_t client_addr_size;
    char buffer[BUFFER_SIZE];

    // Create socket
    if ((server_socket = socket(AF_INET, SOCK_DGRAM, 0)) < 0) {
        perror("socket creation failed");
        exit(EXIT_FAILURE);
    }

    memset(&server_addr, 0, sizeof(server_addr));
    memset(&client_addr, 0, sizeof(client_addr));

    // Configure server address
    server_addr.sin_family = AF_INET;
    server_addr.sin_port = htons(PORT);
    server_addr.sin_addr.s_addr = INADDR_ANY;

    // Bind the socket to the server address
    if (bind(server_socket, (const struct sockaddr *)&server_addr, sizeof(server_addr)) < 0) {
        perror("bind failed");
        exit(EXIT_FAILURE);
    }

    printf("Server started. Waiting for messages...\n");

    while (1) {
        // Receive message from client
        int len = recvfrom(server_socket, (char *)buffer, BUFFER_SIZE, 0,
                           (struct sockaddr *)&client_addr, &client_addr_size);
        buffer[len] = '\0';
        printf("Client: %s\n", buffer);

        // Reply to client
        printf("Server (You): ");
        fgets(buffer, BUFFER_SIZE, stdin);
        sendto(server_socket, (const char *)buffer, strlen(buffer), 0,
               (const struct sockaddr *)&client_addr, client_addr_size);
    }

    close(server_socket);
    return 0;
}

#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <unistd.h>
#include <arpa/inet.h>

#define SERVER_IP "SERVER_IP_ADDRESS" // Replace with the server's IP address
#define PORT 8888
#define BUFFER_SIZE 1024

int main() {
    int client_socket;
    struct sockaddr_in server_addr;

    // Create socket
    if ((client_socket = socket(AF_INET, SOCK_DGRAM, 0)) < 0) {
        perror("socket creation failed");
        exit(EXIT_FAILURE);
    }

    memset(&server_addr, 0, sizeof(server_addr));

    // Configure server address
    server_addr.sin_family = AF_INET;
    server_addr.sin_port = htons(PORT);
    if (inet_pton(AF_INET, SERVER_IP, &server_addr.sin_addr) <= 0) {
        perror("Invalid address/ Address not supported");
        exit(EXIT_FAILURE);
    }

    char buffer[BUFFER_SIZE];

    while (1) {
        // Send message to server
        printf("Client (You): ");
        fgets(buffer, BUFFER_SIZE, stdin);
        sendto(client_socket, (const char *)buffer, strlen(buffer), 0,
               (const struct sockaddr *)&server_addr, sizeof(server_addr));

        // Receive message from server
        int len = recvfrom(client_socket, (char *)buffer, BUFFER_SIZE, 0, NULL, NULL);
        buffer[len] = '\0';
        printf("Server: %s\n", buffer);
    }

    close(client_socket);
    return 0;
}

