from typing import Type

class SourceRegistry:
    registered_sources = set()

    @classmethod
    def register_source(cls, source_class: Type):
        cls.registered_sources.add(source_class)

    @classmethod
    def is_registered(cls, obj):
        return any(isinstance(obj, registered) for registered in cls.registered_sources)


class SourceBase:
    def __init__(self):
        SourceRegistry.register_source(self.__class__)

