    constructor() {}

    call(yTrue, yPred) {
      // Ensuring the predictions are in probability form
      const yProbs = tf.softmax(yPred, -1);

      // Clipping values to avoid log(0)
      const yClipped = tf.clipByValue(yProbs, 1e-7, 1 - 1e-7);

