from scrapy.pipelines.media import MediaPipeline, MediaPipelineDownloadException

class CustomImagesPipeline(MediaPipeline):

    def get_media_requests(self, item, info):
        if self.IMAGES_URLS_FIELD in item:
            return [Request(x) for x in item.get(self.IMAGES_URLS_FIELD, [])]
        else:
            raise MediaPipelineDownloadException('Item contains no images')

    def item_completed(self, results, item, info):
        item = super().item_completed(results, item, info)
        if isinstance(item.get(self.IMAGES_RESULT_FIELD), Exception):
            raise MediaPipelineDownloadException('Error occurred while downloading images')
        return item

