async function updateFlightPlans() {
  try {
    const latestFlightPlans = await fetchLatestFlightPlans();

    // Find all existing flight plans in the database
    const existingFlightPlans = await FlightPlan.find({}, 'callsign');

    // Extract callsigns from the existing flight plans
    const existingCallsigns = existingFlightPlans.map((flightPlan) => flightPlan.callsign);

    // Identify new flights to add
    const newFlightPlans = latestFlightPlans.filter((flightPlan) => !existingCallsigns.includes(flightPlan.callsign));

    // Identify flights to remove
    const callsignsToRemove = existingCallsigns.filter((callsign) => !latestFlightPlans.some((flightPlan) => flightPlan.callsign === callsign));

    // Update existing flights (compare data and update if needed)
    const updatePromises = latestFlightPlans.map(async (flightPlanData) => {
      const existingFlightPlan = await FlightPlan.findOne({ callsign: flightPlanData.callsign });
      if (existingFlightPlan) {
        // Compare the data to check if an update is required
        if (!isEqual(existingFlightPlan.toObject(), flightPlanData)) {
          await FlightPlan.updateOne({ callsign: flightPlanData.callsign }, flightPlanData);
        }
      }
    });

    // Execute all update operations concurrently
    await Promise.all(updatePromises);

    // Perform bulk operations for adding and removing flights
    await FlightPlan.insertMany(newFlightPlans);
    await FlightPlan.deleteMany({ callsign: { $in: callsignsToRemove } });
  } catch (error) {
    console.error('Error updating flight plans:', error);
  }
}

