// GenerateJWT generates a JWT token with a custom claim.
func GenerateJWT() (string, error) {
    // Your secret key to sign the token
    secretKey := []byte("your-secret-key")

    // Create a new token with a claim
    token := jwt.New(jwt.SigningMethodHS256)
    claims := token.Claims.(jwt.MapClaims)
    claims["username"] = "your-username"
    claims["exp"] = time.Now().Add(time.Hour * 24).Unix() // Token expiration time (1 day)

    // Sign the token with your secret key
    tokenString, err := token.SignedString(secretKey)
    if err != nil {
        return "", err
    }

    return tokenString, nil
}

// ValidateJWT validates a JWT token and returns the claims.
func ValidateJWT(tokenString string) (jwt.MapClaims, error) {
    // Your secret key to verify the token
    secretKey := []byte("your-secret-key")

    // Parse the token
    token, err := jwt.Parse(tokenString, func(token *jwt.Token) (interface{}, error) {
        return secretKey, nil
    })

    if err != nil {
        return nil, err
    }

    // Check if the token is valid
    if !token.Valid {
        return nil, errors.New("invalid token")
    }

    // Extract claims from the token
    claims, ok := token.Claims.(jwt.MapClaims)
    if !ok {
        return nil, errors.New("invalid claims")
    }

    return claims, nil
}

func generateJWTHandler(w http.ResponseWriter, r *http.Request) {
    tokenString, err := GenerateJWT()
    if err != nil {
        http.Error(w, "Failed to generate JWT", http.StatusInternalServerError)
        return
    }

    // Send the JWT as a JSON response
    response := map[string]string{"token": tokenString}
    json.NewEncoder(w).Encode(response)
}

