        this.lastRefillTimestamp = now;
    }

    consume(tokensToConsume: number = 1): boolean {
        this.refill();

        if (this.tokens >= tokensToConsume) {
            this.tokens -= tokensToConsume;
            return true;
        }
        return false;
    }
}

class RateLimiter {
    private tokenBuckets: Map<string, TokenBucket>;

    constructor() {
        this.tokenBuckets = new Map();
    }

    // Retrieve or create a new bucket for a given key
    getBucket(key: string, capacity: number, refillRate: number): TokenBucket {
        if (!this.tokenBuckets.has(key)) {
            this.tokenBuckets.set(key, new TokenBucket(capacity, refillRate));
        }
        return this.tokenBuckets.get(key)!;
    }

    consume(key: string, tokensToConsume: number = 1): boolean {
        const bucket = this.tokenBuckets.get(key);
        if (!bucket) {
            throw new Error(`TokenBucket for key '${key}' not found. Make sure to initialize it first with getBucket().`);
        }
        return bucket.consume(tokensToConsume);
    }
}

// Example Usage:
const rateLimiter = new RateLimiter();

// Initialize a specific bucket
rateLimiter.getBucket('someAction', 1, 0.2);

// Try consuming a token for a specific action
if (rateLimiter.consume('someAction')) {
    console.log('Token consumed, perform the action!');
} else {
    console.log('Rate limit exceeded for someAction. Try again later.');
}

