import Image from "next/image";
const ResponseDisplay = ({ data, error, loading }) => {
  let content;

  if (loading) {
    content = "Loading...";
  } else if (error) {
    content = `Error: ${error.message}`;
  } else if (data && data.result.movieSuggestions) {
    // Assuming data.result.movieSuggestions is an array of movie objects with title, description, and imageUrl properties
    content = (
      <div className="movie-list">
        {data.result.movieSuggestions.map((movie, index) => (
          <div className="movie" key={index}>
            <h1>{movie.title}</h1>
            <p>{movie.description}</p>
            <Image src={movie.imageUrl} alt={`Poster for ${movie.title}`} width={500} height={700} />
          </div>
        ))}
      </div>
    );
  } else {
    content = "";
  }

  return <div className="response-display">{content}</div>;
};

export default ResponseDisplay;

