  };
}

export function getUserPrompt(movieName) {
  return {
    role: "user",
    content: `Suggest movies similar to "${movieName}" that I might enjoy. Please include the movie's title, description, ratings, and essential information.`,
  };
}

export function getFunctions() {
  return [
    {
      name: "generate_movie_suggestions",
      description: "Generate movie suggestions based on user input, including movie titles, descriptions, ratings, and essential information.",
      parameters: {
        type: "object",
        properties: {
          movieSuggestions: {
            type: "array",
            items: {
              type: "object",
              properties: {
                title: {
                  type: "string",
                  description: "The title of the suggested movie",
                },
                description: {
                  type: "string",
                  description: "A brief description of the suggested movie",
                },
                imageUrl: {
                  type: "string",
                  description: "An image URL for the suggested movie poster",
