package com.mweuc654.Kiepe;

import android.app.Application;
import android.content.Context;
import android.content.res.Configuration;
import androidx.annotation.NonNull;

import com.facebook.react.BuildConfig;
import com.facebook.react.PackageList;
import com.facebook.react.ReactApplication;
import com.facebook.react.ReactNativeHost;
import com.facebook.react.ReactPackage;
import com.facebook.react.ReactInstanceManager; // Add this import
import com.facebook.react.bridge.ReactContext;
import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.bridge.ReactMarker;
import com.facebook.soloader.SoLoader;

import expo.modules.ApplicationLifecycleDispatcher;
import expo.modules.ReactNativeHostWrapper;

import java.util.List;

public class MainApplication extends Application implements ReactApplication {

  private final ReactNativeHost mReactNativeHost =
    new ReactNativeHostWrapper(this, new DefaultReactNativeHost(this) {
      @Override
      public boolean getUseDeveloperSupport() {
        return BuildConfig.DEBUG;
      }

      @Override
      protected List<ReactPackage> getPackages() {
        @SuppressWarnings("UnnecessaryLocalVariable")
        List<ReactPackage> packages = new PackageList(this).getPackages();
        // Packages that cannot be autolinked yet can be added manually here, for example:
        // packages.add(new MyReactNativePackage());
        return packages;
      }

      @Override
      protected String getJSMainModuleName() {
        return "index";
      }

      @Override
      protected boolean isNewArchEnabled() {
        return false;
      }

      @Override
      protected Boolean isHermesEnabled() {
        return true;
      }
  });

  @Override
  public ReactNativeHost getReactNativeHost() {
    return mReactNativeHost;
  }

  @Override
  public void onCreate() {
    super.onCreate();
    SoLoader.init(this, /* native exopackage */ false);
    if (false) {
      // If you opted-in for the New Architecture, we load the native entry point for this app.
      DefaultNewArchitectureEntryPoint.load();
    }

    // Initialize React Native Flipper only in debug mode
    if (BuildConfig.DEBUG) {
        initializeFlipper(this, mReactNativeHost.getReactInstanceManager());
    }

    ApplicationLifecycleDispatcher.onApplicationCreate(this);
  }

  private static void initializeFlipper(Context context, ReactInstanceManager reactInstanceManager) {
    if (BuildConfig.DEBUG) {
        ReactMarker.logMarker(ReactMarkerConstants.INITIALIZE_FABRIC_START);
        ReactMarker.logMarker(ReactMarkerConstants.INITIALIZE_FABRIC_END);
        ReactMarker.logMarker(ReactMarkerConstants.INITIALIZE_MODULES_START);
        ReactMarker.logMarker(ReactMarkerConstants.INITIALIZE_MODULES_END);

        // For Network plugin
        final ReactApplicationContext reactContext = new ReactApplicationContext(context);
        final FlipperClient client = AndroidFlipperClient.getInstance();
        client.addPlugin(new InspectorFlipperPlugin(context, DescriptorMapping.withDefaults()));
        client.addPlugin(new ReactNativeFlipper(reactContext, new FlipperModuleDescriptorSupplier(reactContext)));
        client.start();
    }
  }

  @Override
  public void onConfigurationChanged(@NonNull Configuration newConfig) {
    super.onConfigurationChanged(newConfig);
    ApplicationLifecycleDispatcher.onConfigurationChanged(this, newConfig);
  }
}

