import random

BOARD_SIZE = 15
EMPTY = '.'
PLAYER = 'X'
AI = 'O'

def print_board(board):
    for row in board:
        print(' '.join(row))
    print()

def is_win(board, x, y, player):
    directions = [(1, 0), (0, 1), (1, 1), (1, -1)]
    for dx, dy in directions:
        count = 0
        for i in range(-4, 5):
            nx, ny = x + i * dx, y + i * dy
            if 0 <= nx < BOARD_SIZE and 0 <= ny < BOARD_SIZE and board[nx][ny] == player:
                count += 1
                if count == 5:
                    return True
            else:
                count = 0
    return False

def ai_move(board, difficulty):
    if difficulty == 'easy':
        while True:
            x, y = random.randint(0, BOARD_SIZE - 1), random.randint(0, BOARD_SIZE - 1)
            if board[x][y] == EMPTY:
                return x, y
    elif difficulty == 'medium':
        for x in range(BOARD_SIZE):
            for y in range(BOARD_SIZE):
                if board[x][y] == EMPTY:
                    board[x][y] = AI
                    if is_win(board, x, y, AI):
                        return x, y
                    board[x][y] = PLAYER
                    if is_win(board, x, y, PLAYER):
                        return x, y
                    board[x][y] = EMPTY
        return ai_move(board, 'easy')

    # For a 'hard' AI, you might want to use Minimax or other algorithms. For simplicity, it's omitted here.

def play_game(difficulty='medium'):
    board = [[EMPTY for _ in range(BOARD_SIZE)] for _ in range(BOARD_SIZE)]
    print_board(board)

    while True:
        x, y = map(int, input("Enter your move (row column): ").split())
        if 0 <= x < BOARD_SIZE and 0 <= y < BOARD_SIZE and board[x][y] == EMPTY:
            board[x][y] = PLAYER
            print_board(board)
            if is_win(board, x, y, PLAYER):
                print("You win!")
                return

            print("AI is thinking...")
            x, y = ai_move(board, difficulty)
            board[x][y] = AI
            print_board(board)
            if is_win(board, x, y, AI):
                print("AI wins!")
                return

if __name__ == "__main__":
    difficulty = input("Choose difficulty (easy/medium): ").lower()
    play_game(difficulty)

import random

class Deck:
    def __init__(self):
        suits = ['Hearts', 'Diamonds', 'Clubs', 'Spades']
        values = ['Two', 'Three', 'Four', 'Five', 'Six', 'Seven', 'Eight', 'Nine', 'Ten', 'Jack', 'Queen', 'King', 'Ace']
        self.cards = [{'suit': suit, 'value': value} for suit in suits for value in values]
        random.shuffle(self.cards)

    def deal(self):
        return self.cards.pop()

class Player:
    def __init__(self, name):
        self.name = name
        self.hand = []

    def add_card(self, card):
        self.hand.append(card)

    def get_hand_value(self):
        value = 0
        aces = 0
        for card in self.hand:
            if card['value'] in ['Jack', 'Queen', 'King']:
                value += 10
            elif card['value'] == 'Ace':
                value += 11
                aces += 1
            else:
                value += int(card['value'][0])

        while value > 21 and aces:
            value -= 10
            aces -= 1
        return value

    def show_hand(self, hide_first_card=False):
        if hide_first_card:
            print(f"{self.name}'s hand: [???, {self.hand[1]['value']} of {self.hand[1]['suit']}] and {len(self.hand) - 2} more cards" if len(self.hand) > 2 else "")
        else:
            hand_str = ', '.join([f"{card['value']} of {card['suit']}" for card in self.hand])
            print(f"{self.name}'s hand: {hand_str}")

def blackjack():
    deck = Deck()
    player = Player("Player")
    dealer = Player("Dealer")

    # Initial dealing
    for _ in range(2):
        player.add_card(deck.deal())
        dealer.add_card(deck.deal())

    player.show_hand()
    dealer.show_hand(hide_first_card=True)

    # Player's turn
    while player.get_hand_value() < 21:
        action = input("Do you want to (H)it or (S)tand? ").lower()
        if action == 'h':
            player.add_card(deck.deal())
            player.show_hand()
        elif action == 's':
            break

    # Dealer's turn
    while dealer.get_hand_value() < 17:
        dealer.add_card(deck.deal())

    # Show results
    player.show_hand()
    dealer.show_hand()

    if player.get_hand_value() > 21:
        print("Player busts! Dealer wins!")
    elif dealer.get_hand_value() > 21:
        print("Dealer busts! Player wins!")
    elif player.get_hand_value() > dealer.get_hand_value():
        print("Player wins!")
    elif dealer.get_hand_value() > player.get_hand_value():
        print("Dealer wins!")
    else:
        print("It's a tie!")

if __name__ == "__main__":
    blackjack()

import os
import json

def read_json_files_in_directory(directory_path):
    data_array = []
    if not os.path.exists(directory_path):
        print(f"The directory '{directory_path}' does not exist.")
        return data_array

    for filename in os.listdir(directory_path):
        file_path = os.path.join(directory_path, filename)
        if os.path.isfile(file_path) and filename.endswith(".json"):
            try:
                with open(file_path, 'r') as json_file:
                    data = json.load(json_file)
                    data_array.append(data)
            except Exception as e:
                print(f"Error reading {file_path}: {str(e)}")

    return data_array

