        console.error(`Unable to parse JSON file: ${file}`);
        return;
      }

      const formattedData = JSON.stringify(jsonData, null, 2);

      // Write the processed file to the output directory
      fs.writeFile(path.join(outputDir, file), formattedData, 'utf-8', err => {
        if (err) {
          console.error(`Unable to write file: ${file}`);
        } else {
          console.log(`Processed file: ${file}`);
        }
      });
    });
  });
});

