handleWatchFileChanges(filePath) {
  try {
    let isWatching = true; // 監視フラグを追加

    fs.watch(filePath, eventType => {
      if (eventType === "change" && isWatching) { // 監視フラグが有効な場合のみ処理を実行
        isWatching = false; // 監視フラグを無効化
        console.log("File changed");
        this.handleLoad(filePath, "utf8");
        setTimeout(() => {
          isWatching = true; // 一定時間後に監視フラグを再度有効化
        }, 1000); // 1秒後に再度監視フラグを有効化する例
      }
    });
  } catch (error) {
    this.Err.errorMain(error);
  }
}

package main

import (
	"fmt"
	"time"

	"github.com/dgrijalva/jwt-go"
)

func main() {
	myToken := "your-jwt-token-here"

	// Parse the token
	token, err := jwt.Parse(myToken, func(token *jwt.Token) (interface{}, error) {
		// Don't forget to validate the alg is what you expect:
		if _, ok := token.Method.(*jwt.SigningMethodHMAC); !ok {
			return nil, fmt.Errorf("Unexpected signing method: %v", token.Header["alg"])
		}
		return []byte("your-secret"), nil
	})

	if err != nil {
		fmt.Println("Error while parsing the token:", err)
		return
	}

	if claims, ok := token.Claims.(jwt.MapClaims); ok && token.Valid {
		// Convert the Unix timestamp from the token to time.Time
		if exp, ok := claims["exp"].(float64); ok {
			// Convert the float64 to an int64
			expTime := time.Unix(int64(exp), 0)
			currTime := time.Now()

			if currTime.After(expTime) {
				fmt.Println("Token is expired")
			} else {
				fmt.Println("Token is still valid")
			}
		} else {
			fmt.Println("Unable to find or convert expiration time")
		}
	} else {
		fmt.Println("Token is not valid")
	}
}

