def validate_extra_context(ctx, param, value):
    """Validate extra context."""
    for key in value:
        if "=" not in key:
            raise click.BadParameter(
                "EXTRA_CONTEXT should contain items of the form key=value; "
                "'{}' doesn't match that form".format(key)
            )

