          // Commit and push the changes
          exec('git commit -a -m "Format code"', (err, stdout, stderr) => {
            if (err) {
              console.error(err);
              return;
            }

            exec(`git push origin ${branchName}`, (err, stdout, stderr) => {
              if (err) {
                console.error(err);
                return;
              }

              console.log('Code formatted and pushed.');
            });
          });
        });
      });
    });
  }
});

