import org.apache.avro.Schema;
import org.apache.avro.generic.GenericData;
import org.apache.avro.generic.GenericRecord;
import org.apache.parquet.avro.AvroParquetWriter;
import org.apache.parquet.hadoop.metadata.CompressionCodecName;
import org.apache.parquet.io.OutputFile;

import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Paths;

public class Main {

  public static void main(String[] args) throws IOException {

    // Define the Avro schema
    String rawSchema = "{\"type\":\"record\",\"name\":\"Test\",\"fields\":[{\"name\":\"field1\",\"type\":\"string\"}]}";
    Schema schema = new Schema.Parser().parse(rawSchema);

    // Create a record to write
    GenericRecord record = new GenericData.Record(schema);
    record.put("field1", "Test data");

    // Define the path and create the Parquet writer
    OutputFile outputFile = new OutputFile(Paths.get("data.parquet").toAbsolutePath(), Files::newOutputStream);
    try (var parquetWriter = AvroParquetWriter
        .<GenericRecord>builder(outputFile)
        .withSchema(schema)
        .withCompressionCodec(CompressionCodecName.SNAPPY)
        .build()) {

      // Write the record to the Parquet file
      parquetWriter.write(record);
    }
  }
}

import Konva from 'konva';

let stage = new Konva.Stage({
  container: 'container',   // id of container <div>
  width: 800,
  height: 600
});

let layer = new Konva.Layer();
stage.add(layer);

let rect = new Konva.Rect({
  x: 20,
  y: 20,
  width: 100,
  height: 100,
  fill: 'red',
  draggable: true
});
layer.add(rect);

let circle = new Konva.Circle({
  x: 200,
  y: 100,
  radius: 50,
  fill: 'blue',
  draggable: true
});
layer.add(circle);

layer.draw();

let transformer = new Konva.Transformer({
  node: rect,
  enabledAnchors: ['top-left', 'top-right', 'bottom-left', 'bottom-right'],
  boundBoxFunc: (oldBox, newBox) => {
    if (newBox.width < 5 || newBox.height < 5) {
      return oldBox;
    }
    return newBox;
  },
});

layer.add(transformer);
layer.draw();

const canvas = new fabric.Canvas('c');

const rect = new fabric.Rect({
  left: 100,
  top: 100,
  fill: 'red',
  width: 60,
  height: 70,
});

canvas.add(rect);

canvas.setActiveObject(rect);

// Customize control points
fabric.Object.prototype.set({
  transparentCorners: false,
  cornerColor: 'blue',
  borderColor: 'blue',
  cornerSize: 25,  // Make the control points larger
  cornerStyle: 'circle',
});

canvas.renderAll();

fabric.Object.prototype.set({
  // Other properties...
  padding: 10,
});

import { fabric } from 'fabric';

const canvas = new fabric.Canvas('c');

fabric.loadSVGFromURL('path/to/logo.svg', function(objects, options) {
  const logo = fabric.util.groupSVGElements(objects, options);

  // Manipulate the SVG object if needed
  logo.set({ left: 10, top: 10 });

  // Then add it to the canvas
  canvas.add(logo).renderAll();
});

import { fabric } from 'fabric';

const canvas = new fabric.Canvas('c');

fabric.loadSVGFromURL('path/to/logo.svg', function(objects, options) {
  const loadedObjects = fabric.util.groupSVGElements(objects, options);

  loadedObjects.forEach(function(obj) {
    canvas.add(obj);
  });

  canvas.renderAll();
});

import { fabric } from 'fabric';

const canvas = new fabric.Canvas('c');

fabric.loadSVGFromURL('path/to/logo.svg', function(objects, options) {
  objects.forEach((object) => {
    if (object.type === 'text') {
      const textbox = new fabric.Textbox(object.text, object.toObject());
      canvas.add(textbox);
    } else {
      canvas.add(object);
    }
  });

  canvas.renderAll();
});

import { fabric } from 'fabric';

const canvas = new fabric.Canvas('c');

fabric.loadSVGFromURL('path/to/logo.svg', function(objects, options) {
  objects.forEach((object) => {
    if (object.type === 'text') {
      const textbox = new fabric.Textbox(object.text, {
        left: object.left,
        top: object.top,
        fill: object.fill,
        fontSize: object.fontSize,
        fontFamily: object.fontFamily,
        fontWeight: object.fontWeight,
        fontStyle: object.fontStyle,
        underline: object.underline,
        linethrough: object.linethrough,
        overline: object.overline,
        textAlign: object.textAlign,
        charSpacing: object.charSpacing,
        // ... any other properties you want to copy
      });
      canvas.add(textbox);
    } else {
      canvas.add(object);
    }
  });

  canvas.renderAll();
});

