  return process.argv[2] === "4" ? "gpt-4" : "gpt-3.5-turbo";
}

async function getSystemPrompt() {
  return (await fs.readFile("prompt/system.md", "utf8")).toString()
}
export { api, rl, get_model, getSystemPrompt};

