const inspect = require('util');
const re = require('re');
const { parse, DocstringStyle } = require('docstring_parser');
const util = require('agency/util');
const { Message } = require('agency/schema');
const { print_warning } = require('agency/util');

// access keys
const ACCESS_PERMITTED = "permitted";
const ACCESS_DENIED = "denied";
const ACCESS_REQUESTED = "requested";

function generateHelp(method) {
    const signature = inspect.signature(method);
    const parsedDocstring = parse(method.__doc__, DocstringStyle.GOOGLE);

    const helpObject = {};

    // description
    if (parsedDocstring.short_description !== null) {
        let description = parsedDocstring.short_description;
        if (parsedDocstring.long_description !== null) {
            description += " " + parsedDocstring.long_description;
        }
        helpObject["description"] = description.replace(/\s+/g, " ").trim();
    }

    // args
    helpObject["args"] = {};
    const docstringArgs = {};
    parsedDocstring.params.forEach(arg => {
        docstringArgs[arg.arg_name] = arg;
    });
    const argNames = Object.keys(signature.parameters).slice(1); // skip 'self' argument
    argNames.forEach(argName => {
        const argObject = {};

        // type
        const sigAnnotation = signature.parameters[argName].annotation;
        if (sigAnnotation !== null && sigAnnotation.name !== "_empty") {
            argObject["type"] = util.python_to_json_type_name(sigAnnotation.name);
        } else if (argName in docstringArgs && docstringArgs[argName].type_name !== null) {
            argObject["type"] = util.python_to_json_type_name(docstringArgs[argName].type_name);
        }

        // description
        if (argName in docstringArgs && docstringArgs[argName].description !== null) {
            argObject["description"] = docstringArgs[argName].description.trim();
        }

        helpObject["args"][argName] = argObject;
    });

    // returns
    if (parsedDocstring.returns !== null) {
        helpObject["returns"] = {};

        // type
        if (signature.return_annotation !== null) {
            helpObject["returns"]["type"] = util.python_to_json_type_name(signature.return_annotation.name);
        } else if (parsedDocstring.returns.type_name !== null) {
            helpObject["returns"]["type"] = util.python_to_json_type_name(parsedDocstring.returns.type_name);
        }

        // description
        if (parsedDocstring.returns.description !== null) {
            helpObject["returns"]["description"] = parsedDocstring.returns.description.trim();
        }
    }

    return helpObject;
}

function action(...args) {
    return function decorator(method) {
        const methodProps = {
            "name": method.name,
            "access_policy": ACCESS_PERMITTED,
            "help": generateHelp(method),
            ...args,
        };
        method.action_properties = methodProps;
        return method;
    }
}

