                    return_value = actionMethod(...Object.values(message.action.args));
                    this._current_message = null;

                    if (return_value !== null) {
                        this.send({
                            "to": message.from,
                            "action": {
                                "name": "response",
                                "args": {
                                    "data": return_value,
                                    "original_message_id": message.id,
                                },
                            }
                        });
                    }
                } else {
                    throw new Error(`${this.id()}.${message.action.name}" not permitted`);
                }
            } catch (e) {
                error = e;
                throw e;
            } finally {
                this.after_action(message, return_value, error);
            }
        } catch (e) {
