  try {
    const jsonData = fs.readFileSync(jsonFilePath, 'utf-8');
    const tasks = JSON.parse(jsonData);

    for (let i = 0; i < tasks.length; i++) {
      const task = tasks[i];
      const { projectKey, projectId, parent, issueType, summary, description, duedate, labels } = task;

      await createTask(projectKey, projectId, parent, parseInt(issueType), summary, description, duedate, labels);

      // Introduce 500 ms delay
      await delay(500);
    }
  } catch (error) {
    console.error('Error creating tasks from JSON:', error);
  }
}

