import com.auth0.jwt.JWT;
import com.auth0.jwt.algorithms.Algorithm;
import com.auth0.jwt.exceptions.JWTCreationException;
import com.auth0.jwt.exceptions.JWTVerificationException;
import com.auth0.jwt.interfaces.DecodedJWT;
import com.auth0.jwt.interfaces.JWTVerifier;

import java.security.interfaces.RSAPrivateKey;
import java.security.interfaces.RSAPublicKey;
import java.util.Date;

public class JwtUtils {
    private static final long EXPIRATION_TIME_MS = 15 * 60 * 1000; // 15 minutes

    private RSAPublicKey rsaPublicKey;
    private RSAPrivateKey rsaPrivateKey;

    public JwtUtils(RSAPublicKey rsaPublicKey, RSAPrivateKey rsaPrivateKey) {
        this.rsaPublicKey = rsaPublicKey;
        this.rsaPrivateKey = rsaPrivateKey;
    }

    public String createJwtToken(User user) {
        try {
            Algorithm algorithm = Algorithm.RSA256(rsaPublicKey, rsaPrivateKey);
            Date expiryDate = new Date(System.currentTimeMillis() + EXPIRATION_TIME_MS);

            String token = JWT.create()
                    .withIssuer("auth0")
                    .withClaim("userId", user.getId())
                    .withExpiresAt(expiryDate)
                    .sign(algorithm);
            return token;
        } catch (JWTCreationException exception) {
            // Handle exception if JWT creation fails
            exception.printStackTrace();
            return null;
        }
    }

    public boolean verifyJwtToken(String token) {
        try {
            Algorithm algorithm = Algorithm.RSA256(rsaPublicKey, rsaPrivateKey);
            JWTVerifier verifier = JWT.require(algorithm)
                    .withIssuer("auth0")
                    .build();
            DecodedJWT decodedJWT = verifier.verify(token);
            return true;
        } catch (JWTVerificationException exception) {
            // Handle exception if JWT verification fails
            exception.printStackTrace();
            return false;
        }
    }

    public String extractUserIdFromToken(String token) {
        try {
            DecodedJWT decodedJWT = JWT.decode(token);
            return decodedJWT.getClaim("userId").asString();
        } catch (JWTVerificationException exception) {
            // Handle exception if JWT decoding fails
            exception.printStackTrace();
            return null;
        }
    }
}

import java.util.ArrayList;
import java.util.List;
import java.util.Optional;

public class UserRepository {
    private List<User> users;

    public UserRepository() {
        users = new ArrayList<>();
        users.add(new User("1", "john", "password123"));
        users.add(new User("2", "jane", "password456"));
        // Add more users as needed
    }

    public Optional<User> findByUsername(String username) {
        return users.stream()
                .filter(user -> user.getUsername().equals(username))
                .findFirst();
    }

    public List<User> getAllUsers() {
        return users;
    }
}

public class AuthenticationService {
    private UserRepository userRepository;
    private JwtUtils jwtUtils;

    public AuthenticationService(UserRepository userRepository, JwtUtils jwtUtils) {
        this.userRepository = userRepository;
        this.jwtUtils = jwtUtils;
    }

    public String authenticate(String username, String password) {
        Optional<User> optionalUser = userRepository.findByUsername(username);
        if (optionalUser.isPresent()) {
            User user = optionalUser.get();
            if (user.getPassword().equals(password)) {
                return jwtUtils.createJwtToken(user);
            }
        }
        return null;
    }

    public boolean isTokenValid(String token) {
        return jwtUtils.verifyJwtToken(token);
    }

    public String extractUserIdFromToken(String token) {
        return jwtUtils.extractUserIdFromToken(token);
    }
}

public class Main {
    public static void main(String[] args) {
        // Replace with your RSA public and private keys
        RSAPublicKey rsaPublicKey = getRSAPublicKey();
        RSAPrivateKey rsaPrivateKey = getRSAPrivateKey();

        UserRepository userRepository = new UserRepository();
        JwtUtils jwtUtils = new JwtUtils(rsaPublicKey, rsaPrivateKey);
        AuthenticationService authService = new AuthenticationService(userRepository, jwtUtils);

        // Simulating a user login
        String username = "john";
        String password = "password123";
        String token = authService.authenticate(username, password);
        if (token != null) {
            System.out.println("Authentication successful. Token: " + token);

            // Simulating a request to fetch all users
            if (authService.isTokenValid(token)) {
                String userId = authService.extractUserIdFromToken(token);
                if (userId != null) {
                    // Fetch all users
                    if (userId.equals("admin")) {
                        List<User> users = userRepository.getAllUsers();
                        System.out.println("All Users: " + users);
                    } else {
                        System.out.println("Unauthorized access. Insufficient privileges.");
                    }
                } else {
                    System.out.println("Failed to extract user ID from token.");
                }
            } else {
                System.out.println("Token validation failed.");
            }
        } else {
            System.out.println("Authentication failed. Invalid username or password.");
        }
    }

    // Your getRSAPublicKey() and getRSAPrivateKey() methods here
}

