class LlamaPreTrainedModel extends PreTrainedModel {
  constructor(config) {
    super(config);
    this.config = new LlamaConfig();
    this.baseModelPrefix = "model";
    this.supportsGradientCheckpointing = true;
    this.noSplitModules = ["LlamaDecoderLayer"];
    this.skipKeysDevicePlacement = "past_key_values";
  }

  initWeights(module) {
    const std = this.config.initializerRange;
    if (module instanceof require('linear')) {
      module.weight.data.normal_(0.0, std);
      if (module.bias) {
        module.bias.data.zero_();
      }
    } else if (module instanceof require('embedding')) {
      module.weight.data.normal_(0.0, std);
      if (module.paddingIdx) {
        module.weight.data[module.paddingIdx].zero_();
      }
    }
  }

  setGradientCheckpointing(module, value = false) {
    if (module instanceof LlamaModel) {
      module.gradientCheckpointing = value;
    }
  }
}

